import {useState} from 'react'
import axios from 'axios';

const ProductForm = (props) => {

    const {product,setProduct} = props;

    const [title,setTitle]= useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('')


    const submitHandler = (e)=> {
        e.preventDefault();

        axios.get('http://localhost:8000/api/product',{
            title,
            price,
            description
        })
        .then( (res)=>{
            console.log(res)
            console.log(res.data)
            setProduct([...product, res.data])
            setTitle('')
            setPrice('')
            setDescription('')
        })
        .catch( (err) => console.log('Client Side Not Working',err) );
    }



    return (
    <div>
        <h1 style={{color:'lightcyan'}}>Product Manager</h1> 

            <form onSubmit={submitHandler}>
                <div style={{ backgroundColor: "black",padding:"10px",margin:"5px", borderRadius:"5px"}}>
                    <label htmlFor="title">Title:</label>
                    <input type="text" value={title} onChange = { (e)=>setTitle(e.target.value)} />
                </div>
                <div style={{ backgroundColor: "black",padding:"10px",margin:"5px", borderRadius:"5px"}}>
                    <label htmlFor="Price">Price $$:</label>
                    <input type="number" min='1' value={price} onChange = { (e)=>setPrice(e.target.value)} />
                </div>
                <div style={{ backgroundColor: "black",padding:"10px",margin:"5px", borderRadius:"5px"}}>
                    <label htmlFor="description">Description:</label>
                    <input type="text" value={description} onChange = { (e)=>setDescription(e.target.value)} />
                </div>
                <p>
                    <button style={{backgroundColor:'blue',color:'white'}}>Create</button>
                </p>
            </form>
        </div>
    )
}

export default ProductForm
