import {useEffect}from 'react'
import axios from 'axios';
import {Link} from'react-router-dom';


const ProductList = (props) => {
    const {deleteItem,product,setProduct} = props;

    const deleteHandler = (productId)=> {
        axios.delete(`http://localhost:8000/api/product/${productId}`)
            .then( res => {
                deleteItem(productId)
            })
            .catch( (err)=> console.log(err))
    }       

    useEffect ( ()=>{
        axios.get('http://localhost:8000/api/product')
            .then( (res)=>{
                console.log(res.data)
                setProduct(res.data)
            })
            .catch( (err)=>console.log(err))
    }, [setProduct])


    return (
    <div >
        <h1>Product List</h1>
            {
                product.map( (product,index)=>{
                    return(
                        <div key={index}>
                            <hr/>
                            <Link to= {`/product/${product._id}` } style={{color:'white',margin:'10px'}}>
                                {product.title}
                            </Link>



                            <Link to= {`/product/edit/${product._id}` } style={{color:'white'}}>
                                <button className='btn btn-info'>Edit</button>
                            </Link>
                            |
                            |
                            <button className='btn btn-danger' onClick={(e)=> {deleteHandler(product._id)} }>Delete</button>
                        </div>
                    )
                })
            }
    </div>
    )
}

export default ProductList
